Method.Implementation:
set oClass = ##class(%ZEN.proxyObject).%New()
set basePack = $LISTTOSTRING($LIST($LISTFROMSTRING(classDefinition.Name, "."), 1, *-1),".")
set oProperties = ##class(%ZEN.proxyObject).%New() set oClass.NAMESPACE = $NAMESPACE set oClass.super = classDefinition.Super set oClass.properties = oProperties set count = classDefinition.Properties.Count() for i = 1:1:count { set oProp = ##class(%ZEN.proxyObject).%New() set p = classDefinition.Properties.GetAt(i) do oProperties.%DispatchSetProperty(p.Name, oProp) do oProp.%DispatchSetProperty("private", p.Private) do oProp.%DispatchSetProperty("readOnly", p.ReadOnly) do oProp.%DispatchSetProperty("type", p.Type) do ..collectAggregation(oData, classDefinition.Name, p.Type, p.Private) do ..collectAggregation(oData, classDefinition.Name, basePack _ "." _ p.Type, p.Private) }
set oMethods = ##class(%ZEN.proxyObject).%New() set oClass.methods = oMethods set count = classDefinition.Methods.Count() for i = 1:1:count { set oMeth = ##class(%ZEN.proxyObject).%New() set met = classDefinition.Methods.GetAt(i) do oMethods.%DispatchSetProperty(met.Name, oMeth) do oMeth.%DispatchSetProperty("private", met.Private) do oMeth.%DispatchSetProperty("returns", met.ReturnType) do oMeth.%DispatchSetProperty("classMethod", met.ClassMethod) }
set oParameters = ##class(%ZEN.proxyObject).%New() set oClass.parameters = oParameters set count = classDefinition.Parameters.Count() for i = 1:1:count { set oPar = ##class(%ZEN.proxyObject).%New() set p = classDefinition.Parameters.GetAt(i) do oParameters.%DispatchSetProperty(p.Name, oPar) do oPar.%DispatchSetProperty("type", p.Type) }
quit oClass

set superParts = $LISTFROMSTRING(baseClassDefinition.Super, ",")
if (oData.inheritance.%DispatchGetProperty(baseClassDefinition.Name) = "") {
    do oData.inheritance.%DispatchSetProperty(baseClassDefinition.Name, ##class(%ZEN.proxyObject).%New())
}
set oInherit = oData.inheritance.%DispatchGetProperty(baseClassDefinition.Name)
for i=1:1:$LISTLENGTH(superParts) {
    set className = ..extendClassFromType($LISTGET(superParts, i))
    do oInherit.%DispatchSetProperty(className, 1)
    if (oData.classes.%DispatchGetProperty(className) = "") {
        set cdef = ##class(%Dictionary.ClassDefinition).%OpenId(className)
        if (cdef '= "") {
            do oData.classes.%DispatchSetProperty(className, ..getClassData(oData, cdef))
            do ..collectInheritance(oData, cdef)
        }
    }
}
quit $$$OK

set classDef = ##class(%Dictionary.ClassDefinition).%OpenId(type)
if (classDef '= "") {
    if (private) { set t = "composition" } else { set t = "aggregation" }
    set oAgg = oData.%DispatchGetProperty(t).%DispatchGetProperty(className)
    if (oAgg = "") {
        set oAgg = ##class(%ZEN.proxyObject).%New()
        do oData.%DispatchGetProperty(t).%DispatchSetProperty(className, oAgg)
    }
} else { quit $$$OK }

do oAgg.%DispatchSetProperty(type, "1..1")
do ..collectClass(oData, classDef)

quit $$$OK

set oData = ##class(%ZEN.proxyObject).%New() set oData.classes = ##class(%ZEN.proxyObject).%New() set oData.inheritance = ##class(%ZEN.proxyObject).%New() set oData.aggregation = ##class(%ZEN.proxyObject).%New() set oData.composition = ##class(%ZEN.proxyObject).%New()
set classes = ##class(%ResultSet).%New("%Dictionary.ClassDefinition:Summary") do classes.Execute() set listLen = $LISTLENGTH($LISTFROMSTRING(rootPackageName, ".")) while (classes.Next()) { set className = classes.Data("Name") set packageName = $LISTTOSTRING($LIST($LISTFROMSTRING(className, "."), 1, listLen), ".") if (packageName = rootPackageName) { set classDef = ##class(%Dictionary.ClassDefinition).%OpenId(className) do oData.classes.%DispatchSetProperty(classDef.Name, ..getClassData(oData, classDef)) do ..collectInheritance(oData, classDef) } }
quit oData
