Method.Implementation:
set oClass = ##class(%ZEN.proxyObject).%New() set oProperties = ##class(%ZEN.proxyObject).%New() set oClass.super = classDefinition.Super set oClass.properties = oProperties set count = classDefinition.Properties.Count() for i = 1:1:count { set oProp = ##class(%ZEN.proxyObject).%New() do oProperties.%DispatchSetProperty(classDefinition.Properties.GetAt(i).Name, oProp) do oProp.%DispatchSetProperty("private", classDefinition.Properties.GetAt(i).Private) do oProp.%DispatchSetProperty("readOnly", classDefinition.Properties.GetAt(i).ReadOnly) do oProp.%DispatchSetProperty("type", classDefinition.Properties.GetAt(i).Type) }
set oMethods = ##class(%ZEN.proxyObject).%New() set oClass.methods = oMethods set count = classDefinition.Methods.Count() for i = 1:1:count { set oMeth = ##class(%ZEN.proxyObject).%New() do oMethods.%DispatchSetProperty(classDefinition.Methods.GetAt(i).Name, oMeth) do oMeth.%DispatchSetProperty("private", classDefinition.Methods.GetAt(i).Private) do oMeth.%DispatchSetProperty("returns", classDefinition.Methods.GetAt(i).ReturnType) }
return oClass

set superParts = $LISTFROMSTRING(baseClassDefinition.Super, ",")
if (oData.inheritance.%DispatchGetProperty(baseClassDefinition.Name) = "") {
    do oData.inheritance.%DispatchSetProperty(baseClassDefinition.Name, ##class(%ZEN.proxyObject).%New())
}
set oInherit = oData.inheritance.%DispatchGetProperty(baseClassDefinition.Name)
for i=1:1:$LISTLENGTH(superParts) {
    set className = $LISTGET(superParts, i)
    do oInherit.%DispatchSetProperty(className, 1)
    if (oData.classes.%DispatchGetProperty(className) = "") {
        set cdef = ##class(%Dictionary.ClassDefinition).%OpenId(className)
        if (cdef '= "") {
            do oData.classes.%DispatchSetProperty(className, ..getClassData(cdef))
            do ..collectInheritance(oData, cdef)
        }
    }
}
return $$$OK
